
public class MakingDecisionWithIfElse {

	public static void main(String[] args) {
		int x=5;
		if (x==5){
			if(x>=5) {
				System.out.println(x+ "Number is greater than 5");
			}
			else {
				System.out.println(x+"Number is smaller than 5");
			}
		}
		// TODO Auto-generated method stub

	}

}
