
public class NumberNegativeAndPositive {

	public static void main(String[] args) {
		double b=0;
		if(b<0.0) {
			System.out.println(b+" Number is negative");
		}
		else if(b>0) {
			System.out.println(b+" Number is positive");
		}
		else {
			System.out.println(b+" Number is zero");
		}
		// TODO Auto-generated method stub

	}

}
