
public class Swap {

	public static void main(String[] args) {
		int m=12;
		int n=21;
		System.out.println("Before Swap:");
		System.out.println("First number"+m);
		System.out.println("Second number"+n);
		int temp=m;
		m=n;
		n=temp;
		System.out.println("After Swap:");
		System.out.println("First number:"+m);
		System.out.println("Second number"+n);
		// TODO Auto-generated method stub
	}

}
